CREATE TABLE users (id mediumint(8),name varchar(255) default NULL,email varchar(255) default NULL);
CREATE TABLE messages (id mediumint(8),title TEXT default NULL,content TEXT default NULL,user varchar(50) default NULL);
CREATE TABLE subjects (id mediumint(8),description TEXT default NULL);
CREATE TABLE message_subjects (message_id varchar(50) default NULL,subject_id varchar(50) default NULL);
